mapper.select:
SELECT T_INSPECT.DELIV_KEY_NO, T_INSPECT.UNIT_NAME , T_INSPECT.INSPECT_STATUS , T_INSPECT.INSPECT_DATE , T_INSPECT.INSPECT_PASS_QUANT , T_INSPECT.INSPECT_ACCOUNT , T_INSPECT.APP_ROUTE_NO , T_INSPECT.APP_ROUTE_NAME , T_INSPECT.INSPECT_SEQ ,T_INSPECT.INSPECT_SECTION_CD as "INSPECT_SECTION_CD" ,T_INSPECT.INSPECT_SECTION_NAME as "INSPECT_SECTION_NAME" ,T_INSPECT.INSPECT_USER_ID as "INSPECT_USER_ID" ,T_INSPECT.INSPECT_USER_NAME as "INSPECT_USER_NAME" ,${@ps.common.util.ItemSettingUtil@getItemSettingSelectSQL("T_INSPECT")} FROM T_INSPECT WHERE T_INSPECT.ORDER_NO = #{identify.orderNo}
AND T_INSPECT.INSPECT_STATUS = '04'
AND T_INSPECT.DELETE_FLG = '0' ORDER BY T_INSPECT.DELIV_KEY_NO,T_INSPECT.INSPECT_VERSION,T_INSPECT.CORRECT_CD

SELECT T_RECEIVE_MNG.DELIV_KEY_NO, T_ORDER.ORDER_SEQ , T_ORDER.ORDER_NO , T_RECEIVE_MNG.LAST_UPD_DATE, T_INSPECT.PRE_SEQ AS "preSeq", T_INSPECT.INSPECT_SEQ AS "inspectSeq", T_INSPECT.CORRECT_CD AS "correctInd", T_INSPECT.INSPECT_VERSION AS "historyNo", T_INSPECT.INSPECT_STATUS AS "status", T_INSPECT.INSPECT_DATE AS "inspectDate.dateOfymd", T_INSPECT.INSPECT_PASS_QUANT AS "successVol.vol", T_INSPECT.UNIT_NAME AS "unitName", T_INSPECT.INSPECT_SECTION_CD AS "inspectSectionCd", T_INSPECT.INSPECT_SECTION_NAME AS "inspectSectionName", T_INSPECT.INSPECT_USER_ID AS "inspectUserId", T_INSPECT.INSPECT_USER_NAME AS "inspectUserName", T_INSPECT.RB_IND AS "rbInd", T_ARRIVAL.ARRIV_EXP_DATE AS "receiveDate.dateOfymd", T_ARRIVAL.DELIV_VOL AS "receiveVol.vol", T_ORDER.REQ_TYPE_IND AS "reqTypeInd", T_ORDER.ITEM_CD AS "itemCd", T_ORDER.ITEM_NAME AS "itemName", T_ORDER.SUP_CD AS "supCd", T_ORDER.SUP_ABBR_NAME AS "supAbbrName", T_ORDER.SUBCON_IND AS "subconInd", T_ORDER.EDI_IND AS "ediInd", T_ORDER.DELIV_PLACE_CD AS "delivPlaceCd", T_ORDER.DELIV_PLACE_NAME AS "delivPlaceName", T_ORDER.ORDER_VOL AS "orderVol.vol", T_ORDER.ORDER_CANCEL_VOL as "orderCancelVol.vol", T_ORDER.ORDER_DELIV_DATE AS "delivDate.dateOfymd", T_SLIP_MNG.SLIP_NO AS EXCLUSIVE_SLIP_NO, T_SLIP_MNG.LAST_UPD_DATE AS EXCLUSIVE_LAST_UPD_DATE, ${@ps.common.util.ItemSettingUtil@getItemSettingSelectSQLDomainAliasNonBinary("T_ORDER","orderExtItem")}, ${@ps.common.util.ItemSettingUtil@getItemSettingSelectSQLDomainAliasNonBinary("T_INSPECT","inspectExtItem")} FROM T_RECEIVE_MNG INNER JOIN T_ORDER ON T_RECEIVE_MNG.ORDER_NO = T_ORDER.ORDER_NO INNER JOIN T_INSPECT ON T_RECEIVE_MNG.NEW_INSPECT_SEQ=T_INSPECT.INSPECT_SEQ INNER JOIN T_ARRIVAL ON T_RECEIVE_MNG.NEW_ARRIVAL_SEQ=T_ARRIVAL.ARRIVAL_SEQ AND T_ARRIVAL.DELETE_FLG='0' LEFT JOIN T_SLIP_MNG ON T_SLIP_MNG.SLIP_NO = T_ORDER.ORDER_NO AND T_SLIP_MNG.DELETE_FLG = '0'
T_RECEIVE_MNG.DELIV_KEY_NO IN
#{keyList.inspectedListIdentify.delivKeyNo.delivKeyNo}
ORDER BY ${searchCondition.columnOrder.columnOrder()}

mapper.update:
UPDATE T_RECEIVE_MNG SET T_RECEIVE_MNG.DELIV_STATUS = #{delivStatus, jdbcType=VARCHAR},
T_RECEIVE_MNG.NEW_INSPECT_SEQ = #{newInspectSeq, jdbcType=DECIMAL},
T_RECEIVE_MNG.LAST_UPD_DATE = '${@fw.common.util.AppUtil@getSysDateYyyyMMddHHmmssSSS()}', T_RECEIVE_MNG.LAST_UPD_COMPANY_CD = '${@fw.security.SecurityUtil@getCompanyCdByEscapeSql()}', T_RECEIVE_MNG.LAST_UPD_SECTION_CD = '${@fw.security.SecurityUtil@getDefaultSectionCdByEscapeSql()}', T_RECEIVE_MNG.LAST_UPD_USER_ID = '${@fw.security.SecurityUtil@getUserIdByEscapeSql()}' WHERE T_RECEIVE_MNG.DELIV_KEY_NO = #{delivKeyNo, jdbcType=VARCHAR}

UPDATE T_INSPECT
  SET APP_ROUTE_NO = #{appRouteNo, jdbcType=DECIMAL} 
  , APP_ROUTE_NAME = #{appRouteName, jdbcType=VARCHAR} 
